import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Resume_model } from 'src/app/Models/Resume_model';
import { ResumeService } from 'src/app/Services/ResumeTaskServer';

@Component({
  selector: 'app-resume',
  templateUrl: './resume.component.html',
  styleUrls: ['./resume.component.css']
})
export class ResumeComponent implements OnInit {

  public ShowTaskListAray: Resume_model[] = [];

  constructor(public s: ResumeService) { }

  async ngOnInit() {
    try
     {
      this.ShowTaskListAray = await this.s.ResumeSave();
     } 
    catch (ex: any) 
    {
      alert(ex.message);
    }
  }

}
<h2>Resume</h2>
<div>
    <table>
        <thead>
            <tr>
                <th>Name</th>
                <th>Task</th>
                <th>Time</th>
                <th>Linq</th>
                <th>Delete</th>
            </tr>
        </thead>

        <tbody>
            <tr *ngFor="let i of ShowTaskListAray">
                <td>{{i.name}}</td>
                <td>{{i.task}}</td>
                <td>{{i.date | time}}</td>
                <td class="detailes_td">
                    <a routerLink={{i.router}} routerLinkActive="active">{{i.router}}</a>
                </td>
                <td>
                    <img src="/assets/images/deletecircleicon.jpg" width="50" height="30" class="icon" (click)="deleteItem()">

                </td>
            </tr>
        </tbody>
    </table>
</div>
