import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FollowDataupdate } from 'src/app/Models/FollowUpdateDataModel';
import { ServiceDataGLFollow } from 'src/app/Services/FollowDataService';

@Component({
  selector: 'app-add-task',
  templateUrl: './add-task.component.html',
  styleUrls: ['./add-task.component.css']
})
export class AddTaskComponent implements OnInit {

  public InformSubmit: boolean = false;
  public showValue: any;
  public IsSelect: boolean = false;
  public UserName: any;
  public BuddyName: any;
  public TaskName: any;
  public time = new Date();
  public task = new FollowDataupdate();
  public SaveTask = new FollowDataupdate()

  constructor(public router: Router, public myAddTask: ServiceDataGLFollow) { }

  ngOnInit(): void {

  }

  public async SaveToTaskList()
  { 
     try
        {
           this.SaveTask.time = this.time
           const addedTask = await this.myAddTask.AddTask(this.SaveTask);
           this.router.navigateByUrl("/Home");
        } 
           catch (error: any)
        {
           alert(error.message);
        }           
  }

  FunSelect(val:any)
  {
    {
      this.IsSelect=true;

      if(val.target.value=="SHIFT 1" || val.target.value=="SHIFT 2"  || val.target.value=="SHIFT 3"  || val.target.value=="SHIFT 4" )
      {
        this.InformSubmit=true;

      }


      this.showValue = val.target.value;
      this.SaveTask.shift=this.showValue;

    }
  }

}
 <h2>Save Data for inspection</h2>
<br>

<form #FormInfo="ngForm">
    <input type="text" placeholder="Name..." class="name" name="name" [(ngModel)]="SaveTask.techname"
    required #nameInfotask="ngModel">
     <span *ngIf="nameInfotask.invalid &&  nameInfotask.touched">Missing Name...</span>
    <br><br>
    <input type="text" placeholder="Buddy..." class="buddy" name="buddy" [(ngModel)]="SaveTask.buddy"
    required #BuddyInfotask="ngModel">
     <span *ngIf="BuddyInfotask.invalid && BuddyInfotask.touched">Missing Buddy Name...</span>
    <br><br>
     <label class="optional">
            <select  aria-label="Shift..." class="select_shift" (click)="FunSelect($event)">
            <option *ngIf="IsSelect">SHIFT 1</option>
            <option *ngIf="IsSelect">SHIFT 2</option>
            <option *ngIf="IsSelect">SHIFT 3</option>
            <option *ngIf="IsSelect">SHIFT 4</option>
        </select>

    </label>


    <br><br><br>
    <input type="text" placeholder="Tool , Task..." class="task" name="task" [(ngModel)]="SaveTask.task"
    required #detailTask="ngModel">
    &nbsp; <span *ngIf="detailTask.invalid && detailTask.touched">Missing Task detailes</span>
    <br><br>
   <button class="submit" [disabled]="FormInfo.form.invalid" (click)="SaveToTaskList()">Submit</button>
</form>
