import { Component, Injectable, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Authentication_permissionsModel } from 'src/app/Models/Authentication_permissionsModel';
import { Creditionals } from 'src/app/Models/Creditionals';
import { SaveUser } from 'src/app/Models/SaveUser';
import { Authentication_permissionsService } from 'src/app/Services/Authentication_permissionsService';
import { ServiceDataGLFollow } from 'src/app/Services/FollowDataService';
import { ModuleService } from 'src/app/Services/ModuleService';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})


@Injectable({
  providedIn: 'root'
})
export class MenuComponent implements OnInit {

  constructor(public ServicePermission : ServiceDataGLFollow ,public router: Router, public Authentication_permissionService : Authentication_permissionsService, public s2: ModuleService) { }
  public IsAccess: boolean=false;
  public User_id: any;
  public Userpass: any;
  public Authentication_permissionsUser: Authentication_permissionsModel   = {}
  public SaveLoginUserIfJWT : Authentication_permissionsModel = {};
  public Authentication_permissionsToken: Authentication_permissionsModel [] = [];

  public IsFailed_UserId: boolean = false;

  public IsFailed_UserPass: boolean = false;

  public Detailes_Uncorrect: boolean = false;

  public done: string="";

  public ToShowPass = false;

  public ToHidePass = false;

  public Select: any;

  public SelectCCC: any;

  public SelectPassdown:  any;

  public cookiesUser: any;

  public cookiesPass: any;

  public cookieShift: any;

  public ActivateURL: any;

  public FirstName: any

  public SaveJWT : string="";

  public date: Date = new Date();

  public Savedate: string = "";

  public ChoiceNavigate : any;

  public creditionals  = new Creditionals();

  public saveUser =  new SaveUser();

  public RefresheCreditional: any;

  public selectNav:any; 

  async ngOnInit() 
  {
      setTimeout(() => {
      this.IfLoginExoireToken();
     },113000)    
  }

 public async IfLoginExoireToken()
 {
  let user  = localStorage.getItem("User")
  this.SaveLoginUserIfJWT=  await this.Authentication_permissionService.GetNewUserCreditionalIfTokenUser_ExpireDate(user);
  this.creditionals.username = this.SaveLoginUserIfJWT.user_name;
  this.creditionals.password = this.SaveLoginUserIfJWT.pass;
  localStorage.removeItem("token");
  localStorage.removeItem("User");
  this.login();
 }

 public  AcceseeArea()
  { 
    alert("Hello");
    if(typeof localStorage.getItem("token") === 'string')
    {
       let ValToken = localStorage.getItem("token")?.toString();
       if(typeof ValToken === 'string' && typeof ValToken != null)
        { 
          this.IsAccess = false;
          alert("TaskList");    
          return;

        }
    }
    this.IsAccess = true;
    return;
  }

  CCC_Chat(val: any) 
   {
    this.ChoiceNavigate = "/CCC"
   }

   Passdown(val: any)
  {
    this.ChoiceNavigate="/TaskList"
  }

 async login()
  {
    try
    {

      this.Authentication_permissionsUser = (await this.Authentication_permissionService.GotToken(this.creditionals));

      if( this.Authentication_permissionsUser.user_name!= null && this.Authentication_permissionsUser.jwt_token != null)
      {
        this.saveUser.username = this.Authentication_permissionsUser.first_name
        this.saveUser.jwt      = this.Authentication_permissionsUser.jwt_token;
        if(this.saveUser.username != null && this.saveUser.jwt != null)
        {
             localStorage.setItem("token" , this.saveUser.jwt);
             localStorage.setItem("User",this.saveUser.username);
             localStorage.setItem("NavigateTo", this.ChoiceNavigate);
             this.IsAccess = false;
             this.router.navigateByUrl(this.ChoiceNavigate);
             return;
        }      
      }     
    }
    catch(ex: any)
    {
      alert("Not Authorized 401" + ex.message);
    }
  }

   cancel()
  {
    this.IsAccess = false;
  }

  ShowPass()
  {
    document.getElementById("pass")?.setAttribute("type", "text");
    this.ToShowPass = false;
    this.ToHidePass = true;
  }

  HidePass()
  {

    document.getElementById("pass")?.setAttribute("type", "password");
    this.ToShowPass = true;
    this.ToHidePass = false;

  }

  CheckIfStart(val: any)
  {

    if(val.target.value.length > 0)
    {
       this.ToShowPass = true;
       this.ToHidePass = false;
    }

    else
    {
      this.ToShowPass = false;
      this.ToHidePass = false;
      document.getElementById("pass")?.setAttribute("type", "password");
    }

  }

}
<div class="AllContent">

    <div class="content">
        <h2>Task List</h2>

        <ul class="menuItems">

            <li>
                <a routerLink="/GAS-Adj" routerLinkActive="active">GAS-Adj</a>
            </li>
            <li>
                <a routerLink="/Macro" routerLinkActive="active">Macro</a>
            </li>
            <li>
                <a routerLink="/PM" routerLinkActive="active">PM</a>
            </li>
            <li>
                <a routerLink="/Home" routerLinkActive="active" value="CCC" [(ngValue)]="selectNav" (click)="AcceseeArea()">CCC</a>
            </li>

             <li>
                <a routerLink="/UpdateData" routerLinkActive="active">Add-Task</a>
            </li> 
            <li>
                <a routerLink="/RESUME" routerLinkActive="active">Resume</a>
            </li>
            <li>
                <a routerLink="/Comments" routerLinkActive="active">Comments</a>
            </li>
            <li>
                <a routerLink="/Home" routerLinkActive="active" value="Tasklist" [(ngModel)]="selectNav" (click)="AcceseeArea()">Task List</a>
            </li>

        </ul>

        <div *ngIf="IsAccess" class="Accesss">
            <form #formInfo="ngForm">
                <input type="text" name="name" placeholder="WW ID..." [(ngModel)]="creditionals.username" class="userName"
                required #NameInfo="ngModel">
                <br>
                <input type="password" name="Userpass" placeholder="Password..." [(ngModel)]="creditionals.password" class="userPass"
                required #PassInfo="ngModel" id="pass" (input)="CheckIfStart($event)"> 
                <img *ngIf="ToShowPass" class="Show" (click)="ShowPass()" src="/assets/images/showPass.jpg" width="25" height="25" class="icon">
                <img *ngIf="ToHidePass" class="Show" (click)="HidePass()" src="/assets/images/HidePass.jpg" width="25" height="25" class="icon">




                <div class="Checkbox">

                    <input type="radio"   value="SelectCCC" name="Datalist" class="point" (change)="CCC_Chat($event)">
                    <span class="AllData">CCC</span>

                   <br>

                    <input type="radio"  value="SelectPassdown" name="Datalist" class="point" (change)="Passdown($event)">
                    <span class="ShiftData">Passdown</span>

                </div>


                    <span *ngIf="IsFailed_UserPass" class="Uncoreect_pass">Password UnCorrect</span>
                    <span *ngIf="IsFailed_UserId" class="Uncorrcect_user">User is Uncorrect</span>
                    <span *ngIf="NameInfo.invalid && NameInfo.touched" class="detailes"></span>

             <div class="LoginOrCancel">
                <button [disabled]="formInfo.form.invalid" class="Logged" (click)="login()">Sign in</button>
                <button (click)="cancel()" class="cancel">Canecl</button>
             </div>      
                <hr>
                <div class="buttonWrapper">
                    <button class="ForgetPass">Forgot Password</button>
                </div>
            </form>
        </div>    

    </div>          

</div>
