import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CommentModel } from 'src/app/Models/CommentModel';
import { CommentsServer } from 'src/app/Services/CommentsServer';


@Component({
  selector: 'app-comments-page',
  templateUrl: './comments-page.component.html',
  styleUrls: ['./comments-page.component.css']
})
export class CommentsPageComponent implements OnInit {

  public ShowName: any;
  public IfMessageSend = false;
  public selectedValue: any;
  public SubjectsArray: string [] =["ENG","PT","MS","Floater","T.S_L3"];
  public NewComment = new CommentModel();
  constructor(public route: Router, public Service: CommentsServer) { }

  ngOnInit(): void {
    this.SubjectsArray;
  }

  Cancel()
  {
    this.route.navigateByUrl("/Home");
  }

 public async Send()
  {
    try
    {
    this.NewComment.subject = this.selectedValue;
    await this.Service.AddComment(this.NewComment);
    this.MessageSendFun();
    }
    catch(ex: any)
    {
    alert(ex.message);
    }
  }

  public MessageSendFun()
  {
    this.IfMessageSend = true;
    setTimeout (() =>{
      this.IfMessageSend = false;
      this.route.navigateByUrl("/Home");
    },10000)
  }

  HideMessage()
  {
    this.IfMessageSend = false;
    this.route.navigateByUrl("/Home");
  }

  Reset(comment: string)
  {
    if(comment.length > 0)
    this.NewComment.comment="";

  }

}


<h2>Comments</h2>
<p>
    Hi All,<br><br>
    Please insert detailes and comment text to improve or insert rubrique to menu.<br><br> 
    Thanks Regards, Lior
</p>

<div class="Container">
    <form #FormInfo="ngForm">

Name * 
<input type="text" class="Name" name="name" [(ngModel)]="NewComment.name"
required #nameInfo="ngModel">
WWid * 
<input type="text" class="WWid" name="wwid" [(ngModel)]="NewComment.wwId"
required #WWIDInfo="ngModel">
<br>
<br>
Subject
<select [(ngModel)]="selectedValue" name="ShowSubject" required #SubjectInfo="ngModel">
    <option *ngFor="let i of SubjectsArray" [ngValue]="i">{{i}}</option>
</select>
<br>
<br>
<textarea class="Add_Comment" name="comment"  [(ngModel)]="NewComment.comment"
required #CommentInfo="ngModel"></textarea>
    <br>
    <button (click)="Reset(CommentInfo.value)" class="reset">Reset</button>
    <br>
<div class="buttons">
    <input type="submit" [disabled]="FormInfo.form.invalid" value="Send" class="but" (click)="Send()">
    <input type="button" value="Cancel" class="cancelation" class="but" (click)="Cancel()">
</div>
</form>

<div *ngIf="IfMessageSend" class="ShowMessageSend" >
Thanks messaage send Secsussfully
<input type="button" value="X" class="Ok" (click)="HideMessage()">
</div>
</div>
