          this.IfAshPc = true;
          this.If_Task_Selct = false;
          this.Pma = true;
          this.Pmb = false;
          this.Pmc = false;
          this.Pmd = false;
          this.Pme = false;
          this.hlc = false;
          this.If_Task_Selct = this.Pma;
