    <div class="labelContainer">
        <h2>Modules</h2>
        <label class="labelItem" *ngFor="let i of modules">
            <input name="module" type="radio" (click)="GetToolsPerModules(i.module)" [(ngModel)]="Module_Value" class="Moduleslabel" value="{{i.module}}">
            {{i.module}}
            <br><br>
        </label>
    </div>

    <div class="ToolsArea">
        <h2>Tools</h2>
     <select [(ngModel)]="selectedValue" class="mod">
         <option *ngFor="let i of ToolsPer_mod" [ngValue]="i.tool">{{i.tool}}</option>
     </select>
    </div>

 </div>

