import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Resume_model } from 'src/app/Models/Resume_model';
import { ResumeService } from 'src/app/Services/ResumeTaskServer';
import { HomeComponent } from '../home/home.component';

@Component({
  selector: 'app-resume',
  templateUrl: './resume.component.html',
  styleUrls: ['./resume.component.css']
})
export class ResumeComponent implements OnInit {

  public ShowTaskListAray: Resume_model[] = [];
  public ActiveRoute: any= this.route.url
  public Id_image: any;
  public Isdelete = false;
  public ToCanceldelete = false;
  public ContactNameTodelete: any;

  constructor(public Home: HomeComponent,public s: ResumeService, public route: Router) { }

  async ngOnInit() {
    this.Home.ngOnInit();
    try
     {
      this.ShowTaskListAray = await this.s.ResumeSave();
     } 
    catch (ex: any) 
    {
      alert(ex.message);
    }
  }

  deleteItem(val:any, name: any)
  {
    this.Id_image = val;
    this.ContactNameTodelete = name;
    this.Isdelete = true;
  }

 async  OkTodeleteMessage()
  {
    try 
      {
        if(this.ToCanceldelete == true)
        {
          this.Isdelete = false;
          return; 
        }
         await this.s.DeleteItemFormTable(this.Id_image);
         setTimeout(() =>{
           window.location.reload();
         },100)   
      } 
      catch (ex: any) 
      {
        alert(ex.message);  
      }
    this.Isdelete = false
  }

  Canceldelete()
  {
    this.ToCanceldelete = true;
    this.Isdelete = false;
    this.ToCanceldelete = false;
  }

}
<h2>Resume</h2>
<div>
    <table>
        <thead>
                <th>Name</th>
                <th>Task</th>
                <th>Time</th>
                <th>Linq</th>
                <th>Delete</th>
        </thead>

        <tbody>
            <tr *ngFor="let i of ShowTaskListAray">
                <td>{{i.name}}</td>
                <td>{{i.task}}</td>
                <td>{{i.date | time}}</td>
                <td class="detailes_td">
                    <a routerLink={{i.router}} routerLinkActive="active">{{i.router}}</a>
                </td>
                <td>
                   <a> 
                    <img src="/assets/images/deletecircleicon.jpg" width="50" height="30" class="icon" (click)="deleteItem(i.id, i.name)">
                   </a> 
                </td>
            </tr>
        </tbody>
    </table>

    <div *ngIf="Isdelete" class="deleteAlarm">
        Hi {{ContactNameTodelete}} this item will delete from Table<br>
            <input type="button" class="OkToAlarm" value="Submit" (click)="OkTodeleteMessage()">
            <input type="button" class="CanceldeleteOption" value="Cancel" (click)="Canceldelete()">   
    </div>
</div>
