import { Component, Injectable, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ChatModel } from 'src/app/Models/ChatModel';
import { ContactArray } from 'src/app/Models/ContactArray';
import { ChatService } from 'src/app/Services/ChatService';
import { ContactsOnlineService } from 'src/app/Services/ContactsOnlineService';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
@Injectable({
  providedIn: 'root'
})
export class HomeComponent implements OnInit {

  public StartIfdate : Date = new Date();
  public contactsArray : ContactArray [] = [];
  public GetAllMessages : ChatModel   [] = [];
  public if_deleteAction = true;

  constructor(public Service_contacts: ContactsOnlineService, public route: Router,
    public ChatService: ChatService ) { }

     ngOnInit():void {
      //  this.deleteAllLogin();
    }

   public async deleteAllLogin()
    {
     if(this.if_deleteAction==true)
      {
      if(this.StartIfdate.getHours() == 21 && this.StartIfdate.getMinutes() >= 21 && this.StartIfdate.getMinutes() <= 23)
      {
           this.contactsArray = await this.Service_contacts.GetAllUsers();
           this.contactsArray.map(async (x) =>{
             await this.Service_contacts.Delete_User(x.name);
           });
            this.GetAllMessages = await this.ChatService.GetAllMessages();
            this.GetAllMessages.map(async (x) =>{
              await this.ChatService.DeleteMessage(x.id);  
            })     
           localStorage.removeItem("User");
           localStorage.removeItem("token");
           alert("All the users are message are clear");
           this.route.navigateByUrl("/Home");
           this.if_deleteAction = false;
           let val = this.StartIfdate.getHours
           return;
          }
        }
       }
    }
