import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Authentication_permissionsModel } from 'src/app/Models/Authentication_permissionsModel';
import { Authentication_permissionsService } from 'src/app/Services/Authentication_permissionsService';

@Component({
  selector: 'app-add-user',
  templateUrl: './add-user.component.html',
  styleUrls: ['./add-user.component.css']
})
export class AddUserComponent implements OnInit {

  constructor(public Authentication_Service : Authentication_permissionsService, public route: Router) { }

  public addUser = new Authentication_permissionsModel();
  public name1: string="123";
  public name2: any;
  public ArrayCommands:    string [] = ["ENG","MS","PT","GL", "POC"]
  public ArrayRoleCommand: string [] = ["Admin", "User"]

  ngOnInit(): void {

  }

  public CheckRoleInsert()
  {
   let counter=0;
   this.ArrayCommands.map((x) => {
      if(x==this.addUser._command)
      counter++;
      return;
   });

   if(counter==0)
   {
      alert("You are insert not correct command")
      this.ngOnInit();
      window.location.reload();
      return;
   }
  }

  public CheckCommandInsert()
  {
   let counter=0;
   this.ArrayCommands.map((x) => {
      if(x==this.addUser._command)
      counter++;
      return;
   });

   if(counter==0)
   {
      alert("You are insert not correct command")
      this.ngOnInit();
      window.location.reload();
      return;
   }
  }

  public async SaveNewUser()
  {
   this.CheckCommandInsert();

     try
        {
           if(localStorage.getItem("User") != null && typeof localStorage.getItem("User") ==='string')
            {
            this.name2 = localStorage.getItem("User")?.toString();
            this.name1 = this.name2.toString();
            }

            const addedTask = await this.Authentication_Service.AddNewUser(this.name1,this.addUser);
            this.route.navigateByUrl("/Home");
        } 
           catch (error: any)
        {
           alert(error.message);
        }           
  }

}
<h2>Save Data for inspection</h2>
<br>

<form #FormInfo="ngForm">
    <input type="text" placeholder="First Name..." class="name" name="name" [(ngModel)]="addUser.first_name"
    required #nameInfotask="ngModel">
     <span *ngIf="nameInfotask.invalid &&  nameInfotask.touched">Missing First Name...</span>
    <br><br>
    <input type="text" placeholder="Last Name..." class="buddy" name="LasrName" [(ngModel)]="addUser.last_name"
    required #LastnameInfo="ngModel">
     <span *ngIf="LastnameInfo.invalid && LastnameInfo.touched">Missing Last Name...</span>
    <br><br>    
    <input type="text" placeholder="User Name..." class="task" name="UserName" [(ngModel)]="addUser.user_name"
    required #UserNameInfo="ngModel">
     <span *ngIf="UserNameInfo.invalid && UserNameInfo.touched">Missing User Name</span>
    <br><br>
    <input type="text" placeholder="Pass..." class="task" name="pass" [(ngModel)]="addUser.pass"
    required #UserPass="ngModel">
     <span *ngIf="UserPass.invalid && UserPass.touched">Missing Password</span>
    <br><br>  
    <input type="text" placeholder="role..." class="task" name="role" [(ngModel)]="addUser.role"
    required #RoleInfo="ngModel">
     <span *ngIf="RoleInfo.invalid && RoleInfo.touched">Missing Role</span>
     <span *ngIf="RoleInfo.valid || RoleInfo.touched">User | Admin</span>

    <br><br>
    <input type="text" placeholder="Command..." class="task" name="command" [(ngModel)]="addUser._command"
    required #CommandInfo="ngModel">
     <span *ngIf="CommandInfo.invalid && CommandInfo.touched">Missing Command</span>
     <span *ngIf="CommandInfo.valid && CommandInfo.touched">MS | ENG | GL | POC</span>

    <br><br>
    <a>
    <button class="submit" [disabled]="FormInfo.form.invalid" (click)="SaveNewUser()">Submit</button>
    </a>
</form>
