import { Component, OnInit } from '@angular/core';
import { Authentication_permissionsModel } from 'src/app/Models/Authentication_permissionsModel';
import { ChatModel } from 'src/app/Models/ChatModel';
import { Creditionals } from 'src/app/Models/Creditionals';
import { Authentication_permissionsService } from 'src/app/Services/Authentication_permissionsService';
import { ChatService } from 'src/app/Services/ChatService';

@Component({
  selector: 'app-ccc',
  templateUrl: './ccc.component.html',
  styleUrls: ['./ccc.component.css']
})
export class CCCComponent implements OnInit {

  public IfLogin     =  false;
  public ShowLogin   =  true;
  public ShowLogout  =  false;
  public IfUserlogin =  false;
  public ShowUser:any; 
  public sendMessage: any;
  public user : Authentication_permissionsModel = {};
  public creditionals  = new Creditionals();
  public AddMessage : ChatModel ={};
  public AllMessages : ChatModel [] = [];
  constructor(public Authentication_permissionService : Authentication_permissionsService, public ChatService: ChatService ) {}

 public async ngOnInit() {

     this.ShowUser = localStorage.getItem("UserCCC");
      setTimeout(async () =>{
      try
      {
           if(localStorage.getItem("tokeCCC") != null)
           { 
             alert("Good");
           this.AllMessages = await this.ChatService.GetAllMessages();  
           }
           else 
           {this.AllMessages = []; 
      }
      catch(ex: any)
      {
        alert(ex.message);
      }
     });


  }


  Open_Login()
  {
    this.IfLogin = true;
  }

  public async Tologin() 
  {
    try 
    {
      this.user = (await this.Authentication_permissionService.GotToken(this.creditionals));
      if(this.user.jwt_token != null && this.user.first_name != null)
      {
         localStorage.setItem("tokeCCC", this.user.jwt_token);
         localStorage.setItem("UserCCC", this.user.first_name);
         this.IfLogin = false;
         this.ShowLogin = false;
         this.IfUserlogin = true;
         this.ShowLogout = true;
      }  
    } 

    catch (ex :any) 
    {
      alert(ex.message);
    } 
  }

  public async SendMessgae()
  {
    try
    {
        this.AddMessage.message = this.sendMessage;
        await this.ChatService.AddMessageFun(this.AddMessage);
        this.ngOnInit();
        window.location.reload();
    }

    catch(ex: any)
    {
      alert(ex.message);
    }
  }

  LogOut()
  {
    localStorage.removeItem("tokeCCC");
    localStorage.removeItem("UserCCC");
    this.ShowLogin = true;
    this.ShowLogout = false;
    this.IfUserlogin = false;
  }

  ToCancel() 
  {
    this.IfLogin = false;

  }

}
