export class MaintanancePMComponent {
  constructor(ServiceTorque, Modules, router, CCC) {
    this.ServiceTorque = ServiceTorque;
    this.Modules = Modules;
    this.router = router;
    this.CCC = CCC;
    this.str = this.Modules.ToolChoice;
    this.pcArrayGTO = [this.str + "_PC1", this.str + "_PC2", this.str + "_PC3", this.str + "_PC4", this.str + "_PC5", this.str + "_PC6"];
    this.pcArrayGTA = [this.str + "_PC1", this.str + "_PC2", this.str + "_PC3", this.str + "_PC4", this.str + "_PC5", this.str + "_PC6", this.str + "_PC7", this.str + "_PC8"];
    this.PcEmptyArray = [];
    this.IfSelect_Task = false;
    this.IfTask_define = false;
    this.IfUseTorque = false;
    this.i_Preperation = 1;
    this.Select_Preperation_Picture = "preperation" + this.i_Preperation;
    this.preperation_choice = false;
    this.i_disassembly = 1;
    this.Select_DisAssembly_Picture = "dissassembly" + this.i_disassembly;
    this.i_assembly = 1;
    this.Select_Assembly_Picture = "Assembly" + this.i_assembly;
    this.disassembly_choice = false;
    this.Pma = false;
    this.Pmb = false;
    this.Pmc = false;
    this.Pmd = false;
    this.Pme = false;
    this.hlc = false;
    this.If_Task_Selct = false;
    this.ArraySizeOf_Images_Preperation = [];
    this.ArraySizeOf_Images_DisAssembly = [];
    this.ArraySizeOf_Images_Assembly = [];
    this.ShowModuleChoice = this.Modules.ModuleChoice;
    this.Assembly_choice = false;
    this.IfRegularPc = false;
    this.IfAshPc = false;
    this.ShowModuleChoiceIfAsh = this.Modules.ModuleChoice + "Ash";
    this.IfLogin = false;
    this.ShowName = localStorage.getItem("User");
    this.TorqueListArray = [];
    this.TorqueListArrayAPerTask = [];
    this.IfAskTorqueCheck = false;
    this.IfEdit = false;
  }

  ngOnInit() {
    if (this.IfLogin == true) this.ShowName = localStorage.getItem("User");
    setInterval(() => {
      if (localStorage.getItem("User") != null) this.IfLogin = true;else this.IfLogin = false;
    }, 1000);
    if (this.str.charAt(2) == 'A') this.PcEmptyArray = this.pcArrayGTA;else if (this.str.charAt(2) == 'O') this.PcEmptyArray = this.pcArrayGTO;
  }

  LogoutFun() {
    this.CCC.LogOut();
  }

  Torque() {
    var _this = this;

    return _asyncToGenerator(function* () {
      if (localStorage.getItem("token") == null) alert("You have to login for Torque data list");else {
        _this.preperation_choice = false;
        _this.Assembly_choice = false;
        _this.disassembly_choice = false;
        _this.TorqueListArrayAPerTask = [];
        _this.TorqueListArray = yield _this.ServiceTorque.GetTorqueListPerModule(_this.ShowModuleChoice);

        _this.TorqueListArray.map(x => {
          if (x.pm == _this.showTask) _this.TorqueListArrayAPerTask.push(x);
        });

        _this.IfAskTorqueCheck = true;
      }
    })();
  }

  Tocancel() {
    this.preperation_choice = false;
    this.Assembly_choice = false;
    this.disassembly_choice = false;
  }

  Cancel() {
