import { Component, Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { HomeComponent } from './components/home/home.component';
import { ChatModel } from './Models/ChatModel';
import { ContactArray } from './Models/ContactArray';
import { ChatService } from './Services/ChatService';
import { ContactsOnlineService } from './Services/ContactsOnlineService';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
@Injectable({
  providedIn: 'root'
})
export class AppComponent {
  public Checkdate: Date = new Date();
  public contactsArray : ContactArray [] = [];
  public GetAllMessages : ChatModel   [] = [];
  public if_deleteAction = true;
  public saveTime_Whendelete: any;
  title = 'IntelProject';

  constructor(public Service_contacts: ContactsOnlineService, public route: Router,
    public ChatService: ChatService ) { }

public async ngOnInit()
{
  setInterval(async () =>{

      if(this.Checkdate.getHours() == 1 && this.Checkdate.getMinutes() == 12 && this.d)
      {
          alert("here");
           this.contactsArray = await this.Service_contacts.GetAllUsers();
           this.contactsArray.map(async (x) =>{
             await this.Service_contacts.Delete_User(x.name);
           });
            this.GetAllMessages = await this.ChatService.GetAllMessages();
            this.GetAllMessages.map(async (x) =>{
              await this.ChatService.DeleteMessage(x.id);  
            }) 

           localStorage.removeItem("User");
           localStorage.removeItem("token");
           window.location.reload();
           alert("All the users are message are clear");
           this.route.navigateByUrl("/Home");
           return;
        }


  },65000)

}

}
