import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Authentication_permissionsModel } from 'src/app/Models/Authentication_permissionsModel';
import { FollowDataupdate } from 'src/app/Models/FollowUpdateDataModel';
import { Authentication_permissionsService } from 'src/app/Services/Authentication_permissionsService';
import { ServiceDataGLFollow } from 'src/app/Services/FollowDataService';

@Component({
  selector: 'app-show-tasklist-table',
  templateUrl: './show-tasklist-table.component.html',
  styleUrls: ['./show-tasklist-table.component.css']
})
export class ShowTasklistTableComponent implements OnInit {

  public ActiveRoute: any = "TaskList";
  public Datalist: FollowDataupdate   [] = [];
  public BuDataList: FollowDataupdate [] = [];
  public If_refresh: boolean = false;
  public ShowContact: Authentication_permissionsModel[] = [];
  constructor(public s: ServiceDataGLFollow, public route: Router, public contacts : Authentication_permissionsService ) { }
  public showName = this.s.SaveJWT;


  async ngOnInit() {
    try
     {
       if(this.If_refresh == true)
       {
          this.Datalist   = this.BuDataList;
          this.If_refresh = false;
       }

       this.Datalist = await this.s.GetAllTasksList();
     }
    catch (ex: any)
    {
       alert(ex.message);
    }
 }

 async deleteItem(val:any, name: any)
 {

   //  alert("Hi" +" " + name + ", If you sure that do you want to delete please select OK" +" id:  " +" " + val);
  try
  {
   this.ShowContact = await this.contacts.GetAllAccessPremissionsList();
   this.ShowContact.map((x) => {
    if(x.first_name == this.showName && x.role == "Admin")
     this.If_refresh = true;
     return;
  });
 }
 catch(ex: any)
 {
   alert(ex.message);
 }

  if(this.If_refresh)
  {
  try 
   { alert("welcome");
     this.If_refresh = false;
     alert("Here wa are1");
    await this.s.DeleteItemFormGLTable(val); 
     alert("Here wa are");
     this.route.navigateByUrl("/Home")

      //  setTimeout(()=>{
      //   //  window.location.reload();
      //   this.If_refresh = true;
      //   this.Ifrefrshing();
      //   this.ngOnInit();
      //     },1500)  
  } 
   catch (ex: any) 
   {
     alert(ex.message);  

   }

  }

  else
  alert("UnAuthorized");

 }

  public async  Ifrefrshing() 
 {
   if(this.If_refresh == true)
   {
     alert("good");
     this.s.SaveJWT = this.showName;
     this.BuDataList = await this.s.GetAllTasksList();
     return;
   }

   return;
 }

}
